# FADO CRM - Email Notification System # He thong email thong minh nhu Jarvis! import smtplib import ssl from email.mime.text import MIMEText from email.mime.multipart import MIMEMultipart from email.mime.base import MIMEBase from email import encoders from typing import List, Optional, Dict, Any from datetime import datetime import os from pathlib import Path import json from logging_config import app_logger from models import DonHang, KhachHang, TrangThaiDonHang class EmailService: def __init__(self): self.smtp_server = os.getenv("SMTP_SERVER", "smtp.gmail.com") self.smtp_port = int(os.getenv("SMTP_PORT", "587")) self.email_user = os.getenv("EMAIL_USER", "your-email@gmail.com") self.email_password = os.getenv("EMAIL_PASSWORD", "your-app-password") self.from_name = os.getenv("EMAIL_FROM_NAME", "FADO.VN CRM") # Templates directory self.templates_dir = Path(__file__).parent / "email_templates" self.templates_dir.mkdir(exist_ok=True) # Create default templates if not exist self.create_default_templates() app_logger.info(" Email service initialized successfully") def create_default_templates(self): """ Tao cac template email mac dinh""" templates = { "order_created.html": self.get_order_created_template(), "order_confirmed.html": self.get_order_confirmed_template(), "order_shipped.html": self.get_order_shipped_template(), "order_delivered.html": self.get_order_delivered_template(), "welcome.html": self.get_welcome_template(), } for filename, content in templates.items(): template_path = self.templates_dir / filename if not template_path.exists(): with open(template_path, 'w', encoding='utf-8') as f: f.write(content) def send_email( self, to_emails: List[str], subject: str, html_body: str, text_body: Optional[str] = None, attachments: Optional[List[str]] = None ) -> bool: """ Gui email voi HTML template dep mat""" try: # Tao message message = MIMEMultipart("alternative") message["Subject"] = subject message["From"] = f"{self.from_name} <{self.email_user}>" message["To"] = ", ".join(to_emails) # Add text part if text_body: text_part = MIMEText(text_body, "plain", "utf-8") message.attach(text_part) # Add HTML part html_part = MIMEText(html_body, "html", "utf-8") message.attach(html_part) # Add attachments if attachments: for file_path in attachments: if os.path.isfile(file_path): with open(file_path, "rb") as attachment: part = MIMEBase('application', 'octet-stream') part.set_payload(attachment.read()) encoders.encode_base64(part) part.add_header( 'Content-Disposition', f'attachment; filename= {os.path.basename(file_path)}' ) message.attach(part) # Gui email context = ssl.create_default_context() with smtplib.SMTP(self.smtp_server, self.smtp_port) as server: server.starttls(context=context) server.login(self.email_user, self.email_password) server.sendmail(self.email_user, to_emails, message.as_string()) app_logger.success(f" Email sent successfully to {', '.join(to_emails)}") return True except Exception as e: app_logger.error(f" Failed to send email to {', '.join(to_emails)}: {str(e)}") return False def send_order_notification(self, don_hang: DonHang, event_type: str) -> bool: """ Gui thong bao don hang theo trang thai""" try: customer = don_hang.khach_hang if not customer or not customer.email: app_logger.warning(f" No email found for order {don_hang.ma_don_hang}") return False # Tao context cho template context = { "customer_name": customer.ho_ten, "order_code": don_hang.ma_don_hang, "order_total": don_hang.tong_tien, "order_date": don_hang.ngay_tao.strftime("%d/%m/%Y"), "order_status": don_hang.trang_thai.value, "customer_email": customer.email, "company_name": "FADO.VN", "company_phone": "0123-456-789", "company_email": "support@fado.vn" } # Chon template va subject dua tren event template_map = { "created": ("order_created.html", f" Don hang #{don_hang.ma_don_hang} da duoc tao"), "confirmed": ("order_confirmed.html", f" Don hang #{don_hang.ma_don_hang} da duoc xac nhan"), "shipped": ("order_shipped.html", f" Don hang #{don_hang.ma_don_hang} dang duoc van chuyen"), "delivered": ("order_delivered.html", f" Don hang #{don_hang.ma_don_hang} da duoc giao thanh cong") } if event_type not in template_map: app_logger.warning(f" Unknown event type: {event_type}") return False template_file, subject = template_map[event_type] html_body = self.render_template(template_file, context) return self.send_email( to_emails=[customer.email], subject=subject, html_body=html_body ) except Exception as e: app_logger.error(f" Failed to send order notification: {str(e)}") return False def send_welcome_email(self, customer: KhachHang) -> bool: """ Gui email chao mung khach hang moi""" try: if not customer.email: return False context = { "customer_name": customer.ho_ten, "customer_email": customer.email, "company_name": "FADO.VN", "company_phone": "0123-456-789", "company_email": "support@fado.vn" } html_body = self.render_template("welcome.html", context) return self.send_email( to_emails=[customer.email], subject=" Chao mung ban den voi FADO.VN!", html_body=html_body ) except Exception as e: app_logger.error(f" Failed to send welcome email: {str(e)}") return False def render_template(self, template_name: str, context: Dict[str, Any]) -> str: """ Render email template voi context data""" try: template_path = self.templates_dir / template_name if not template_path.exists(): app_logger.error(f" Template not found: {template_name}") return self.get_fallback_template(context) with open(template_path, 'r', encoding='utf-8') as f: template = f.read() # Simple template rendering (replace {{variable}} voi gia tri) for key, value in context.items(): template = template.replace(f"{{{{{key}}}}}", str(value)) return template except Exception as e: app_logger.error(f" Error rendering template {template_name}: {str(e)}") return self.get_fallback_template(context) def get_fallback_template(self, context: Dict[str, Any]) -> str: """ Template du phong khi co loi""" return f""" <html> <body style="font-family: Arial, sans-serif; line-height: 1.6; color: #333;"> <div style="max-width: 600px; margin: 0 auto; padding: 20px;"> <h2 style="color: #3498db;">Thong bao tu FADO.VN</h2> <p>Xin chao {context.get('customer_name', 'Khach hang')},</p> <p>Cam on ban da su dung dich vu cua FADO.VN!</p> <hr style="border: 1px solid #eee;"> <p style="font-size: 12px; color: #666;"> Email nay duoc gui tu dong tu he thong FADO.VN CRM. </p> </div> </body> </html> """ def get_order_created_template(self) -> str: """ Template don hang duoc tao""" return """ <!DOCTYPE html> <html lang="vi"> <head> <meta charset="UTF-8"> <meta name="viewport" content="width=device-width, initial-scale=1.0"> <title>Don hang da duoc tao</title> </head> <body style="font-family: 'Segoe UI', Tahoma, Geneva, Verdana, sans-serif; line-height: 1.6; color: #333; margin: 0; padding: 0; background-color: #f4f4f4;"> <div style="max-width: 600px; margin: 20px auto; background: white; border-radius: 10px; overflow: hidden; box-shadow: 0 5px 15px rgba(0,0,0,0.1);"> <!-- Header --> <div style="background: linear-gradient(135deg, #667eea 0%, #764ba2 100%); color: white; padding: 30px; text-align: center;"> <h1 style="margin: 0; font-size: 28px;"> FADO.VN</h1> <p style="margin: 10px 0 0; opacity: 0.9;">Mua ho chuyen nghiep</p> </div> <!-- Content --> <div style="padding: 40px 30px;"> <h2 style="color: #2c3e50; margin-bottom: 20px;"> Don hang da duoc tao thanh cong!</h2> <p>Xin chao <strong>{{customer_name}}</strong>,</p> <p>Cam on ban da tin tuong su dung dich vu mua ho cua FADO.VN. Don hang cua ban da duoc tao thanh cong voi thong tin sau:</p> <div style="background: #f8f9fa; border-left: 4px solid #3498db; padding: 20px; margin: 20px 0; border-radius: 5px;"> <div style="display: flex; justify-content: space-between; margin-bottom: 10px;"> <span><strong>Ma don hang:</strong></span> <span style="color: #3498db; font-weight: bold;">{{order_code}}</span> </div> <div style="display: flex; justify-content: space-between; margin-bottom: 10px;"> <span><strong>Ngay tao:</strong></span> <span>{{order_date}}</span> </div> <div style="display: flex; justify-content: space-between; margin-bottom: 10px;"> <span><strong>Tong tien:</strong></span> <span style="color: #e74c3c; font-weight: bold; font-size: 18px;">{{order_total:,.0f}} VND</span> </div> <div style="display: flex; justify-content: space-between;"> <span><strong>Trang thai:</strong></span> <span style="background: #ffeaa7; color: #d63031; padding: 4px 12px; border-radius: 15px; font-size: 12px;">Cho xac nhan</span> </div> </div> <p><strong> Buoc tiep theo:</strong></p> <ul style="color: #666; margin-left: 20px;"> <li>Chung toi se xem xet va xac nhan don hang trong vong 24 gio</li> <li>Ban se nhan duoc email xac nhan khi don hang duoc duyet</li> <li>Qua trinh mua hang va van chuyen se duoc cap nhat thuong xuyen</li> </ul> <div style="text-align: center; margin: 30px 0;"> <a href="mailto:{{company_email}}" style="background: #3498db; color: white; padding: 12px 30px; text-decoration: none; border-radius: 5px; display: inline-block; font-weight: bold;"> Lien he ho tro</a> </div> </div> <!-- Footer --> <div style="background: #2c3e50; color: white; padding: 20px; text-align: center; font-size: 14px;"> <p style="margin: 0;"> Hotline: {{company_phone}} | Email: {{company_email}}</p> <p style="margin: 5px 0 0; opacity: 0.8;"> 2024 FADO.VN - Dich vu mua ho uy tin #1 Viet Nam</p> </div> </div> </body> </html> """ def get_order_confirmed_template(self) -> str: """ Template don hang duoc xac nhan""" return """ <!DOCTYPE html> <html lang="vi"> <head> <meta charset="UTF-8"> <meta name="viewport" content="width=device-width, initial-scale=1.0"> <title>Don hang da duoc xac nhan</title> </head> <body style="font-family: 'Segoe UI', Tahoma, Geneva, Verdana, sans-serif; line-height: 1.6; color: #333; margin: 0; padding: 0; background-color: #f4f4f4;"> <div style="max-width: 600px; margin: 20px auto; background: white; border-radius: 10px; overflow: hidden; box-shadow: 0 5px 15px rgba(0,0,0,0.1);"> <!-- Header --> <div style="background: linear-gradient(135deg, #00b894 0%, #00cec9 100%); color: white; padding: 30px; text-align: center;"> <h1 style="margin: 0; font-size: 28px;"> Don hang da xac nhan</h1> <p style="margin: 10px 0 0; opacity: 0.9;">FADO.VN - Mua ho chuyen nghiep</p> </div> <!-- Content --> <div style="padding: 40px 30px;"> <h2 style="color: #2c3e50; margin-bottom: 20px;"> Tuyet voi! Don hang da duoc xac nhan</h2> <p>Xin chao <strong>{{customer_name}}</strong>,</p> <p>Don hang <strong>{{order_code}}</strong> cua ban da duoc xac nhan thanh cong! Chung toi se bat dau qua trinh mua hang ngay.</p> <div style="background: #d1f2eb; border-left: 4px solid #00b894; padding: 20px; margin: 20px 0; border-radius: 5px;"> <div style="text-align: center;"> <div style="background: #00b894; color: white; padding: 8px 20px; border-radius: 20px; display: inline-block; margin-bottom: 15px;"> D X C NH N </div> <h3 style="margin: 0; color: #00b894;">Don hang: {{order_code}}</h3> <p style="font-size: 18px; font-weight: bold; color: #e74c3c; margin: 5px 0;">{{order_total:,.0f}} VND</p> </div> </div> <p><strong> Timeline du kien:</strong></p> <div style="margin: 20px 0;"> <div style="display: flex; align-items: center; margin-bottom: 15px;"> <div style="width: 30px; height: 30px; background: #00b894; color: white; border-radius: 50%; display: flex; align-items: center; justify-content: center; margin-right: 15px;">1</div> <div> <div style="font-weight: bold;">Dang mua hang</div> <div style="color: #666; font-size: 14px;"> oc tinh 2-5 ngay</div> </div> </div> <div style="display: flex; align-items: center; margin-bottom: 15px;"> <div style="width: 30px; height: 30px; background: #ddd; color: white; border-radius: 50%; display: flex; align-items: center; justify-content: center; margin-right: 15px;">2</div> <div> <div style="color: #666;">Van chuyen quoc te</div> <div style="color: #666; font-size: 14px;"> oc tinh 7-14 ngay</div> </div> </div> <div style="display: flex; align-items: center;"> <div style="width: 30px; height: 30px; background: #ddd; color: white; border-radius: 50%; display: flex; align-items: center; justify-content: center; margin-right: 15px;">3</div> <div> <div style="color: #666;">Giao hang tan noi</div> <div style="color: #666; font-size: 14px;"> oc tinh 1-2 ngay</div> </div> </div> </div> <p style="background: #fff3cd; border: 1px solid #ffeaa7; color: #856404; padding: 15px; border-radius: 5px; margin: 20px 0;"> <strong>Luu y:</strong> Chung toi se thuong xuyen cap nhat trang thai don hang qua email va SMS. </p> </div> <!-- Footer --> <div style="background: #2c3e50; color: white; padding: 20px; text-align: center; font-size: 14px;"> <p style="margin: 0;"> Hotline: {{company_phone}} | Email: {{company_email}}</p> <p style="margin: 5px 0 0; opacity: 0.8;"> 2024 FADO.VN - Cam ket chat luong va uy tin</p> </div> </div> </body> </html> """ def get_order_shipped_template(self) -> str: """ Template don hang dang van chuyen""" return """ <!DOCTYPE html> <html lang="vi"> <head> <meta charset="UTF-8"> <meta name="viewport" content="width=device-width, initial-scale=1.0"> <title>Don hang dang duoc van chuyen</title> </head> <body style="font-family: 'Segoe UI', Tahoma, Geneva, Verdana, sans-serif; line-height: 1.6; color: #333; margin: 0; padding: 0; background-color: #f4f4f4;"> <div style="max-width: 600px; margin: 20px auto; background: white; border-radius: 10px; overflow: hidden; box-shadow: 0 5px 15px rgba(0,0,0,0.1);"> <!-- Header --> <div style="background: linear-gradient(135deg, #fd79a8 0%, #fdcb6e 100%); color: white; padding: 30px; text-align: center;"> <h1 style="margin: 0; font-size: 28px;"> Dang van chuyen</h1> <p style="margin: 10px 0 0; opacity: 0.9;">Hang cua ban dang tren duong!</p> </div> <!-- Content --> <div style="padding: 40px 30px;"> <h2 style="color: #2c3e50; margin-bottom: 20px;"> Don hang dang duoc giao den ban!</h2> <p>Xin chao <strong>{{customer_name}}</strong>,</p> <p>Tin tot! Don hang <strong>{{order_code}}</strong> da duoc dong goi va dang tren duong den dia chi cua ban.</p> <div style="background: #ffeaa7; border-left: 4px solid #fdcb6e; padding: 20px; margin: 20px 0; border-radius: 5px;"> <div style="text-align: center;"> <div style="font-size: 48px; margin-bottom: 15px;"> </div> <h3 style="margin: 0; color: #d63031;">Don hang: {{order_code}}</h3> <p style="margin: 10px 0; color: #666;">Dang van chuyen den ban</p> <div style="background: #fdcb6e; color: white; padding: 8px 20px; border-radius: 20px; display: inline-block;"> DANG V N CHUY N </div> </div> </div> <p><strong> Thong tin giao hang:</strong></p> <ul style="color: #666; margin-left: 20px;"> <li>Thoi gian du kien: 1-3 ngay lam viec</li> <li>Giao hang trong gio hanh chinh</li> <li>Shipper se goi dien truoc khi giao</li> <li>Vui long chuan bi tien mat hoac the de thanh toan COD</li> </ul> <div style="text-align: center; margin: 30px 0;"> <p style="margin-bottom: 15px; color: #666;">Co cau hoi ve don hang?</p> <a href="tel:{{company_phone}}" style="background: #fd79a8; color: white; padding: 12px 25px; text-decoration: none; border-radius: 25px; display: inline-block; margin: 5px;"> Goi ngay</a> <a href="mailto:{{company_email}}" style="background: #74b9ff; color: white; padding: 12px 25px; text-decoration: none; border-radius: 25px; display: inline-block; margin: 5px;"> Email</a> </div> </div> <!-- Footer --> <div style="background: #2c3e50; color: white; padding: 20px; text-align: center; font-size: 14px;"> <p style="margin: 0;"> Hotline: {{company_phone}} | Email: {{company_email}}</p> <p style="margin: 5px 0 0; opacity: 0.8;"> 2024 FADO.VN - Giao hang nhanh chong, an toan</p> </div> </div> </body> </html> """ def get_order_delivered_template(self) -> str: """ Template don hang da giao""" return """ <!DOCTYPE html> <html lang="vi"> <head> <meta charset="UTF-8"> <meta name="viewport" content="width=device-width, initial-scale=1.0"> <title>Don hang da duoc giao thanh cong</title> </head> <body style="font-family: 'Segoe UI', Tahoma, Geneva, Verdana, sans-serif; line-height: 1.6; color: #333; margin: 0; padding: 0; background-color: #f4f4f4;"> <div style="max-width: 600px; margin: 20px auto; background: white; border-radius: 10px; overflow: hidden; box-shadow: 0 5px 15px rgba(0,0,0,0.1);"> <!-- Header --> <div style="background: linear-gradient(135deg, #00b894 0%, #55a3ff 100%); color: white; padding: 30px; text-align: center;"> <h1 style="margin: 0; font-size: 28px;"> Giao hang thanh cong!</h1> <p style="margin: 10px 0 0; opacity: 0.9;">Cam on ban da tin tuong FADO.VN</p> </div> <!-- Content --> <div style="padding: 40px 30px;"> <div style="text-align: center; margin-bottom: 30px;"> <div style="font-size: 72px; margin-bottom: 20px;"> </div> <h2 style="color: #2c3e50; margin: 0;">Don hang da duoc giao thanh cong!</h2> </div> <p>Xin chao <strong>{{customer_name}}</strong>,</p> <p>Chuc mung! Don hang <strong>{{order_code}}</strong> da duoc giao den ban thanh cong. Hy vong ban hai long voi san pham!</p> <div style="background: #d1f2eb; border-left: 4px solid #00b894; padding: 25px; margin: 25px 0; border-radius: 5px; text-align: center;"> <div style="background: #00b894; color: white; padding: 10px 25px; border-radius: 25px; display: inline-block; margin-bottom: 15px;"> D GIAO TH NH C NG </div> <h3 style="margin: 10px 0; color: #00b894;">Don hang: {{order_code}}</h3> <p style="margin: 0; color: #666; font-size: 14px;">Giao thanh cong vao {{order_date}}</p> </div> <div style="background: #fff3cd; border: 1px solid #ffeaa7; color: #856404; padding: 20px; border-radius: 8px; margin: 25px 0;"> <h4 style="margin: 0 0 15px; color: #856404;"> Danh gia dich vu</h4> <p style="margin: 0;"> kien cua ban rat quan trong voi chung toi! Vui long danh 2 phut de danh gia chat luong dich vu va san pham.</p> <div style="text-align: center; margin-top: 15px;"> <a href="#" style="background: #f39c12; color: white; padding: 10px 20px; text-decoration: none; 