# FADO CRM - Basic Tests # Simple tests to verify testing framework is working import pytest from fastapi.testclient import TestClient from main import app def test_root_endpoint(): """Test basic root endpoint""" client = TestClient(app) response = client.get("/") assert response.status_code == 200 data = response.json() assert data["success"] is True assert "FADO.VN CRM API" in data["message"] def test_app_creation(): """Test app is created properly""" assert app is not None assert app.title == " FADO.VN CRM API" def test_basic_math(): """Test basic functionality""" assert 2 + 2 == 4 assert "test" == "test" # Basic tests hoan thanh! 